describe("Abandoned Checkouts",(()=>{beforeEach((()=>{cy.intercept({method:"POST",path:"**/surecart/v1/checkouts/*"},{id:"test",object:"checkout",status:"draft"}).as("createUpdate")})),it("Sends abandoned_checkout_return_url with request",(async()=>{cy.visit("/test/sc-checkout/abandoned-checkout"),cy.wait("@createUpdate").then((({request:e})=>{expect(e.body.abandoned_checkout_enabled).to.eq(!0),expect(e.body.abandoned_checkout_return_url).to.be.undefined,expect(e.body.metadata.page_url).to.include("/test/sc-checkout/abandoned-checkout"),expect(e.body.metadata.page_id).to.eq(1),expect(e.body.metadata.buy_page_product_id).to.eq("testproductid")}))}))}));